      const recentEpisodes = logsWithHeadaches
        .sort((a, b) => new Date(b.date).getTime() - new Date(a.date).getTime())
        .slice(0, parseInt(limit as string))
        .map(log => {
          // Parse headache data for calculations
          let headacheData = log.headacheData;
