@tailwind base;
@tailwind components;
@tailwind utilities;

/* Prevent white screen flashes during navigation */
html, body, #root {
  background-color: #1a1a2e !important;
  margin: 0;
  padding: 0;
  min-height: 100vh;
}

/* Splash Screen Animations */
@keyframes fade-in {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes pulse-logo {
  0%, 100% {
    transform: scale(1);
    box-shadow: 0 0 0 0 rgba(59, 130, 246, 0.7);
  }
  50% {
    transform: scale(1.05);
    box-shadow: 0 0 0 10px rgba(59, 130, 246, 0);
  }
}

.animate-fade-in {
  animation: fade-in 1s ease-out forwards;
}

.animate-pulse-logo {
  animation: pulse-logo 2s infinite;
}

.text-shadow-lg {
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.3);
}

/* Mobile-optimized utility classes */
.active\:scale-98:active {
  transform: scale(0.98);
}

/* Better mobile touch targets */
@media (max-width: 640px) {
  input, textarea, button {
    min-height: 44px; /* iOS recommended touch target */
  }

  /* Prevent zoom on iOS input focus */
  input[type="text"], 
  input[type="number"], 
  textarea {
    font-size: 16px;
  }
}

:root {
  --background: hsl(0, 0%, 100%);
  --foreground: hsl(20, 14.3%, 4.1%);
  --muted: hsl(60, 4.8%, 95.9%);
  --muted-foreground: hsl(25, 5.3%, 44.7%);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(20, 14.3%, 4.1%);
  --card: hsl(0, 0%, 100%);
  --card-foreground: hsl(20, 14.3%, 4.1%);
  --border: hsl(20, 5.9%, 90%);
  --input: hsl(20, 5.9%, 90%);
  --primary: hsl(207, 90%, 54%);
  --primary-foreground: hsl(211, 100%, 99%);
  --secondary: hsl(60, 4.8%, 95.9%);
  --secondary-foreground: hsl(24, 9.8%, 10%);
  --accent: hsl(60, 4.8%, 95.9%);
  --accent-foreground: hsl(24, 9.8%, 10%);
  --destructive: hsl(0, 84.2%, 60.2%);
  --destructive-foreground: hsl(60, 9.1%, 97.8%);
  --ring: hsl(20, 14.3%, 4.1%);
  --radius: 0.5rem;

  /* Zivora custom colors */
  --zivora-dark: hsl(240, 55%, 8%);
  --zivora-purple: hsl(267, 100%, 68%);
  --zivora-gray: hsl(0, 0%, 67%);
}

.dark {
  --background: hsl(240, 10%, 3.9%);
  --foreground: hsl(0, 0%, 98%);
  --muted: hsl(240, 3.7%, 15.9%);
  --muted-foreground: hsl(240, 5%, 64.9%);
  --popover: hsl(240, 10%, 3.9%);
  --popover-foreground: hsl(0, 0%, 98%);
  --card: hsl(240, 10%, 3.9%);
  --card-foreground: hsl(0, 0%, 98%);
  --border: hsl(240, 3.7%, 15.9%);
  --input: hsl(240, 3.7%, 15.9%);
  --primary: hsl(207, 90%, 54%);
  --primary-foreground: hsl(211, 100%, 99%);
  --secondary: hsl(240, 3.7%, 15.9%);
  --secondary-foreground: hsl(0, 0%, 98%);
  --accent: hsl(240, 3.7%, 15.9%);
  --accent-foreground: hsl(0, 0%, 98%);
  --destructive: hsl(0, 62.8%, 30.6%);
  --destructive-foreground: hsl(0, 0%, 98%);
  --ring: hsl(240, 4.9%, 83.9%);
  --radius: 0.5rem;
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen', 'Ubuntu', 'Cantarell', 'Fira Sans', 'Droid Sans', 'Helvetica Neue', sans-serif;
    /* Hide browser UI for native app experience */
    -webkit-user-select: none;
    user-select: none;
    -webkit-touch-callout: none;
    -webkit-tap-highlight-color: transparent;
    overscroll-behavior: none;
    /* Allow scrolling for mobile */
    overflow-y: auto;
    overflow-x: hidden;
    /* Enhanced mobile scrolling */
    -webkit-overflow-scrolling: touch;
    scroll-behavior: smooth;
    /* Remove default margins and padding */
    margin: 0;
    padding: 0;
    min-height: 100vh;
    width: 100vw;
  }

  html {
    overflow-y: auto;
    overflow-x: hidden;
    min-height: 100vh;
    width: 100vw;
    margin: 0;
    padding: 0;
    /* Enhanced mobile scrolling */
    -webkit-overflow-scrolling: touch;
    scroll-behavior: smooth;
  }

  #root {
    min-height: 100vh;
    width: 100vw;
    overflow-y: auto;
    overflow-x: hidden;
    /* Enhanced mobile scrolling */
    -webkit-overflow-scrolling: touch;
  }

  /* Remove status bar and browser chrome simulation */
  .mobile-status-bar {
    display: none !important;
  }

  /* Hide safe area indicators for web version */
  .home-indicator {
    display: none !important;
  }
}

/* Zivora utility classes */
.bg-zivora-dark {
  background-color: var(--zivora-dark);
}

.text-zivora-purple {
  color: var(--zivora-purple);
}

.text-zivora-gray {
  color: var(--zivora-gray);
}

/* Mobile scrolling enhancement */
.mobile-scroll {
  overflow-y: auto;
  overflow-x: hidden;
  -webkit-overflow-scrolling: touch;
  scroll-behavior: smooth;
  overscroll-behavior: contain;
}

/* Ensure all mobile screens are scrollable */
.mobile-screen {
  min-height: 100vh;
  overflow-y: auto;
  overflow-x: hidden;
  -webkit-overflow-scrolling: touch;
  scroll-behavior: smooth;
}

.border-zivora-purple {
  border-color: var(--zivora-purple);
}

.shadow-zivora-purple {
  --tw-shadow-color: var(--zivora-purple);
}

/* Custom animations for splash screen */
@keyframes gentle-bounce {
  0%, 100% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-4px);
  }
}

.animate-gentle-bounce {
  animation: gentle-bounce 2s infinite;
}

@keyframes fade-in {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.animate-fade-in {
  animation: fade-in 0.8s ease-out;
}

@keyframes pulse-purple {
  0%, 100% {
    box-shadow: 0 0 0 0 var(--zivora-purple);
  }
  50% {
    box-shadow: 0 0 0 8px transparent;
  }
}

.animate-pulse-purple {
  animation: pulse-purple 2s infinite;
}

/* iOS Text Input Session Fixes */
/* Prevent iOS RTI input session conflicts */
input, textarea {
  -webkit-appearance: none;
  -webkit-border-radius: 0;
  border-radius: 0;
  -webkit-user-select: text;
  user-select: text;
  -webkit-touch-callout: default;
}

/* Prevent iOS zoom on input focus */
input[type="text"],
input[type="email"],
input[type="password"],
textarea {
  font-size: 16px !important;
  transform: translateZ(0);
  -webkit-backface-visibility: hidden;
  backface-visibility: hidden;
}

/* iOS Capacitor specific fixes */
.capacitor-app input,
.capacitor-app textarea {
  -webkit-user-select: text;
  user-select: text;
  -webkit-touch-callout: default;
  -webkit-tap-highlight-color: transparent;
}

/* Prevent text input session conflicts on focus */
input:focus,
textarea:focus {
  outline: none;
  -webkit-tap-highlight-color: transparent;
  -webkit-user-select: text;
  user-select: text;
}

/* Fix for iOS keyboard resize issues */
.ios .keyboard-is-open {
  height: 100vh;
  overflow: hidden;
}

/* iOS Auto Layout constraint fixes */
/* Prevent constraint conflicts in navigation areas */
.capacitor-app {
  -webkit-transform: translateZ(0);
  transform: translateZ(0);
}

/* Reduce layout conflicts in iOS WebView */
body.capacitor-app {
  position: relative;
  width: 100vw;
  min-height: 100vh;
  -webkit-overflow-scrolling: touch;
  overflow-x: hidden;
}

/* iOS navigation bar constraint fixes */
@supports (-webkit-appearance: none) {
  .mobile-header,
  .navigation-bar,
  .status-bar {
    -webkit-transform: translate3d(0, 0, 0);
    transform: translate3d(0, 0, 0);
    will-change: transform;
  }
}

