import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { ArrowLeft, Search, BookOpen, Clock, ChevronRight, Play, FileText } from "lucide-react";
import { useQuery } from "@tanstack/react-query";
import BottomNavigation from './BottomNavigation';
import EducationCard from './EducationCard';

interface EducationalContent {
  id: number;
  title: string;
  category: string;
  contentType: string;
  description: string;
  readTime: number;
  difficulty: string;
  tags: string[];
  rating?: number;
  isCompleted?: boolean;
  progress?: number;
}

interface RecentContent {
  id: number;
  title: string;
  lastAccessed: string;
}

export default function EducationScreen() {
  const [location, navigate] = useLocation();
  const [activeTab, setActiveTab] = useState('All Topics');

  // Get authenticated user ID from token
  const getUserIdFromToken = () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) return null;
      const payload = JSON.parse(atob(token.split('.')[1]));
      return payload.userId;
    } catch {
      return null;
    }
  };

  const userId = getUserIdFromToken() || 2;

  // Fetch categories dynamically
  const { data: categories, isLoading: categoriesLoading } = useQuery({
    queryKey: ['/api/educational-content/categories'],
    queryFn: async () => {
      const token = localStorage.getItem("token");
      const response = await fetch('/api/educational-content/categories', {
        headers: { "Authorization": `Bearer ${token}` }
      });
      if (!response.ok) throw new Error('Failed to fetch categories');
      const cats = await response.json();
      return ['All Topics', ...cats];
    }
  });

  // Fetch educational content with category filtering
  const { data: educationalContent, isLoading: contentLoading } = useQuery({
    queryKey: ['/api/educational-content', activeTab],
    queryFn: async () => {
      const token = localStorage.getItem("token");
      const url = activeTab === 'All Topics' 
        ? '/api/educational-content'
        : `/api/educational-content?category=${encodeURIComponent(activeTab)}`;
      const response = await fetch(url, {
        headers: { "Authorization": `Bearer ${token}` }
      });
      if (!response.ok) throw new Error('Failed to fetch educational content');
      return response.json();
    }
  });

  // Fetch user progress
  const { data: userProgress } = useQuery({
    queryKey: ['/api/user-progress'],
    queryFn: async () => {
      const token = localStorage.getItem("token");
      const response = await fetch('/api/user-progress', {
        headers: { "Authorization": `Bearer ${token}` }
      });
      if (!response.ok) throw new Error('Failed to fetch user progress');
      return response.json();
    }
  });

  // Fetch recently viewed content
  const { data: recentContent } = useQuery({
    queryKey: ['/api/educational-content/recent'],
    queryFn: async () => {
      const token = localStorage.getItem("token");
      const response = await fetch('/api/educational-content/recent', {
        headers: { "Authorization": `Bearer ${token}` }
      });
      if (!response.ok) throw new Error('Failed to fetch recent content');
      return response.json();
    }
  });

  // Handle content click and mark as viewed
  const handleContentClick = async (contentId: number) => {
    try {
      const token = localStorage.getItem("token");
      await fetch(`/api/educational-content/${contentId}/view`, {
        method: 'POST',
        headers: { "Authorization": `Bearer ${token}` }
      });
    } catch (error) {
      console.error('Failed to mark content as viewed:', error);
    }
  };

  // Format time ago for recently viewed
  const getTimeAgo = (dateString: string) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffInDays = Math.floor((now.getTime() - date.getTime()) / (1000 * 60 * 60 * 24));

    if (diffInDays === 0) return 'Today';
    if (diffInDays === 1) return '1 day ago';
    return `${diffInDays} days ago`;
  };

  if (contentLoading || categoriesLoading) {
    return (
      <div className="min-h-screen bg-[#0F172A] flex items-center justify-center">
        <div className="text-white text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-purple-500 mx-auto mb-4"></div>
          <p>Loading Education Content...</p>
        </div>
      </div>
    );
  }

  // Show error state if no data available
  if (!educationalContent || educationalContent.length === 0) {
    return (
      <div className="min-h-screen bg-[#0F172A] text-white pb-20">
        <div className="flex items-center justify-between p-4 bg-[#1E293B]">
          <button
            onClick={() => navigate('/dashboard')}
            className="flex items-center justify-center w-10 h-10 rounded-full bg-gray-700 hover:bg-gray-600 transition-colors"
          >
            <ArrowLeft className="w-5 h-5 text-white" />
          </button>
          <h1 className="text-white text-lg font-semibold">Education</h1>
          <button className="flex items-center justify-center w-10 h-10 rounded-full bg-gray-700 hover:bg-gray-600 transition-colors">
            <Search className="w-5 h-5 text-white" />
          </button>
        </div>

        <div className="flex flex-col items-center justify-center h-96">
          <BookOpen className="w-16 h-16 text-gray-500 mb-4" />
          <h3 className="text-white text-lg font-semibold mb-2">No Content Available</h3>
          <p className="text-gray-400 text-center px-8">Educational content will appear here when available.</p>
        </div>

        <BottomNavigation />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-[#0F172A] text-white pb-20">
      {/* Header */}
      <div className="flex items-center justify-between p-4 bg-[#1E293B]">
        <button
          onClick={() => navigate('/dashboard')}
          className="flex items-center justify-center w-10 h-10 rounded-full bg-gray-700 hover:bg-gray-600 transition-colors"
        >
          <ArrowLeft className="w-5 h-5 text-white" />
        </button>
        <h1 className="text-white text-lg font-semibold">Education</h1>
        <button className="flex items-center justify-center w-10 h-10 rounded-full bg-gray-700 hover:bg-gray-600 transition-colors">
          <Search className="w-5 h-5 text-white" />
        </button>
      </div>

      <div className="p-4">
        {/* Featured Content Card */}
        <div 
          className="rounded-xl p-4 mb-6 relative transition-all duration-300 hover:shadow-[0_0_8px_rgba(150,100,255,0.3)]"
          style={{
            background: 'linear-gradient(#12141D, #12141D) padding-box, linear-gradient(90deg, #975EFF, #53D4FF) border-box',
            border: '1px solid transparent'
          }}
        >
          <div className="flex items-start gap-4">
            <div 
              className="w-12 h-12 rounded-full flex items-center justify-center flex-shrink-0"
              style={{ backgroundColor: '#181A27' }}
            >
              <BookOpen className="w-5 h-5" style={{ color: '#A88CFF' }} />
            </div>
            <div className="flex-1">
              <h3 className="text-white text-base font-semibold mb-1.5 leading-tight">Understanding Migraine Triggers</h3>
              <p className="text-sm leading-snug mb-2" style={{ color: '#AAAAAA' }}>Learn how to identify and manage your personal migraine triggers</p>
              <p className="text-xs" style={{ color: '#888888' }}>10 min read</p>
            </div>
          </div>
        </div>

        {/* Category Tabs */}
        <div className="flex space-x-3 mb-6 overflow-x-auto pb-1">
          {categories?.map((category) => (
            <button
              key={category}
              onClick={() => setActiveTab(category)}
              className={`px-5 py-2.5 rounded-full text-sm font-semibold whitespace-nowrap transition-all duration-200 ${
                activeTab === category
                  ? 'bg-purple-600 text-white shadow-lg shadow-purple-600/25'
                  : 'bg-[#1E293B] text-gray-300 hover:bg-[#2A3441] hover:text-white'
              }`}
            >
              {category}
            </button>
          ))}
        </div>

        {/* Content List */}
        <div className="space-y-4 mb-8">
          {educationalContent?.map((content: EducationalContent) => (
            <EducationCard
              key={content.id}
              id={content.id}
              title={content.title}
              description={content.description}
              readTime={content.readTime}
              category={content.category}
              contentType={content.contentType}
              difficulty={content.difficulty}
              tags={content.tags}
              onClick={() => handleContentClick(content.id)}
            />
          ))}
        </div>

        {/* Recommended Reading Section - Dynamic content only */}
        {educationalContent?.filter(content => content.contentType === 'course' || content.difficulty === 'advanced').length > 0 && (
          <div className="mb-8">
            <h3 className="text-white font-semibold text-lg mb-4">Recommended Reading</h3>
            <div className="space-y-3">
              {educationalContent
                ?.filter(content => content.contentType === 'course' || content.difficulty === 'advanced')
                .slice(0, 2)
                .map((content) => (
                  <div key={content.id} className="bg-[#1A1D29] rounded-2xl p-4">
                    <div className="flex items-start space-x-4">
                      <div className="w-12 h-12 bg-gradient-to-br from-purple-500 to-purple-700 rounded-xl flex items-center justify-center flex-shrink-0">
                        <BookOpen className="w-6 h-6 text-white" />
                      </div>
                      <div className="flex-1">
                        <h4 className="text-white font-semibold mb-1 text-base">{content.title}</h4>
                        <p className="text-gray-400 text-sm mb-2">{content.description}</p>
                        <div className="flex items-center space-x-3">
                          <div className="flex items-center space-x-1">
                            <Clock className="w-3 h-3 text-gray-400" />
                            <span className="text-gray-400 text-sm">{content.readTime} min</span>
                          </div>
                          <span className="text-blue-400 text-sm">{content.difficulty}</span>
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
            </div>
          </div>
        )}

        {/* Quick Tips Section - Dynamic content only */}
        {educationalContent?.filter(content => content.contentType === 'tip').length > 0 && (
          <div className="mb-8">
            <h3 className="text-white font-semibold text-lg mb-4">Quick Tips</h3>
            <div className="space-y-3">
              {educationalContent
                ?.filter(content => content.contentType === 'tip')
                .slice(0, 3)
                .map((tip) => (
                  <div key={tip.id} className="bg-[#1A1D29] rounded-xl p-4 border-l-4 border-purple-500">
                    <h4 className="text-white font-semibold mb-2">{tip.title}</h4>
                    <p className="text-gray-400 text-sm leading-relaxed">{tip.description}</p>
                  </div>
                ))}
            </div>
          </div>
        )}

        {/* Recently Viewed Section - Dynamic content only */}
        {recentContent && recentContent.length > 0 && (
          <div className="mb-8">
            <h3 className="text-white font-semibold text-lg mb-4">Recently Viewed</h3>
            <div className="space-y-3">
              {recentContent.map((content: any) => (
                <div key={content.id} className="flex items-center justify-between bg-[#1A1D29] rounded-xl p-4">
                  <div className="flex items-center space-x-3">
                    <div className="w-10 h-10 bg-purple-600 rounded-full flex items-center justify-center">
                      <Clock className="w-5 h-5 text-white" />
                    </div>
                    <div>
                      <h4 className="text-white text-base font-medium">{content.title}</h4>
                      <p className="text-gray-400 text-sm">{getTimeAgo(content.lastAccessed || content.createdAt)}</p>
                    </div>
                  </div>
                  <ChevronRight className="w-5 h-5 text-gray-400" />
                </div>
              ))}
            </div>
          </div>
        )}
      </div>

      <BottomNavigation />
    </div>
  );
}

client/src/components/ExportHealthDataSc.content:
import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { ArrowLeft, Download, FileText, File, Moon, Activity, Utensils, Heart } from "lucide-react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { format } from "date-fns";

interface ExportPreferences {
  dateRange: 'last7' | 'last30' | 'custom';
  customStartDate: string;
  customEndDate: string;
  dataTypes: {
    sleepQuality: boolean;
    stressLevels: boolean;
    foodTriggers: boolean;
    hrv: boolean;
  };
  format: 'csv' | 'pdf';
}

export default function ExportHealthDataScreen() {
  const [location, navigate] = useLocation();
  const queryClient = useQueryClient();
  const [userId, setUserId] = useState<number | null>(null);
  const [preferences, setPreferences] = useState<ExportPreferences>({
    dateRange: 'last30',
    customStartDate: '',
    customEndDate: '',
    dataTypes: {
      sleepQuality: true,
      stressLevels: true,
      foodTriggers: false,
      hrv: true
    },
    format: 'csv'
  });

  useEffect(() => {
    const userData = localStorage.getItem("user");
    if (userData) {
      const parsedUser = JSON.parse(userData);
      setUserId(parsedUser.id);
    } else {
      navigate("/get-started");
    }
  }, [navigate]);

  // Fetch export preferences
  const { data: savedPreferences } = useQuery({
    queryKey: ['/api/export/preferences', userId],
    enabled: !!userId,
  });

  // Fetch data summary for size calculation
  const { data: dataSummary } = useQuery({
    queryKey: ['/api/export/summary', userId, preferences.dateRange, preferences.customStartDate, preferences.customEndDate],
    enabled: !!userId,
  });

  // Export mutation
  const exportMutation = useMutation({
    mutationFn: async (exportData: any) => {
      const response = await fetch('/api/export/health-data', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify(exportData)
      });

      if (!response.ok) {
        throw new Error('Export failed');
      }

      return response.blob();
    },
    onSuccess: (blob) => {
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.style.display = 'none';
      a.href = url;
      a.download = `health-data-${preferences.dateRange}-${preferences.format}.${preferences.format}`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
    }
  });

  // Initialize preferences from saved data
  useEffect(() => {
    if (savedPreferences) {
      setPreferences(prev => ({ ...prev, ...savedPreferences }));
    }
  }, [savedPreferences]);

  const handleDateRangeChange = (range: 'last7' | 'last30' | 'custom') => {
    setPreferences(prev => ({ ...prev, dateRange: range }));
  };

  const handleDataTypeToggle = (type: keyof ExportPreferences['dataTypes']) => {
    setPreferences(prev => ({
      ...prev,
      dataTypes: {
        ...prev.dataTypes,
        [type]: !prev.dataTypes[type]
      }
    }));
  };

  const handleFormatChange = (format: 'csv' | 'pdf') => {
    setPreferences(prev => ({ ...prev, format }));
  };

  const handleCustomDateChange = (field: 'customStartDate' | 'customEndDate', value: string) => {
    setPreferences(prev => ({ ...prev, [field]: value }));
  };

  const handleExport = () => {
    if (!userId) return;

    const exportData = {
      userId,
      ...preferences,
      selectedDataTypes: Object.entries(preferences.dataTypes)
        .filter(([_, selected]) => selected)
        .map(([type]) => type)
    };

    exportMutation.mutate(exportData);
  };

  const getDateRangeText = () => {
    switch (preferences.dateRange) {
      case 'last7':
        return 'Last 7 days';
      case 'last30':
        return 'Last 30 days';
      case 'custom':
        if (preferences.customStartDate && preferences.customEndDate) {
          return `${preferences.customStartDate} to ${preferences.customEndDate}`;
        }
        return 'Custom range';
      default:
        return 'Last 30 days';
    }
  };

  const getSelectedDataTypesCount = () => {
    return Object.values(preferences.dataTypes).filter(Boolean).length;
  };

  const getEstimatedSize = () => {
    if (dataSummary?.estimatedSize) {
      return dataSummary.estimatedSize;
    }
    return '~2.4 MB';
  };

  return (
    <div className="min-h-screen bg-[#0F0F23] text-white">
      {/* Header */}
      <div className="flex items-center justify-between px-4 pt-16 pb-6">
        <button
          onClick={() => navigate('/settings')}
          className="p-2 hover:bg-gray-700 rounded-full transition-colors"
        >
          <ArrowLeft className="w-6 h-6 text-white" />
        </button>
        <h1 className="text-lg font-semibold">Export Health Data</h1>
        <div className="w-10"></div>
      </div>

      <div className="px-4 space-y-6">
        {/* Select Date Range */}
        <div className="bg-[#1A1F2E] rounded-xl p-4 border border-[#2A3441]/30">
          <h2 className="text-white font-medium mb-4">Select Date Range</h2>

          <div className="space-y-3">
            <button
              onClick={() => handleDateRangeChange('last7')}
              className={`w-full flex items-center p-3 rounded-lg transition-colors ${
                preferences.dateRange === 'last7' 
                  ? 'bg-[#7C3AED] text-white' 
                  : 'bg-transparent text-gray-300 hover:bg-gray-700/50'
              }`}
            >
              <div className={`w-4 h-4 rounded-full mr-3 ${
                preferences.dateRange === 'last7' ? 'bg-white' : 'border-2 border-gray-500'
              }`}></div>
              Last 7 days
            </button>

            <button
              onClick={() => handleDateRangeChange('last30')}
              className={`w-full flex items-center p-3 rounded-lg transition-colors ${
                preferences.dateRange === 'last30' 
                  ? 'bg-[#7C3AED] text-white' 
                  : 'bg-transparent text-gray-300 hover:bg-gray-700/50'
              }`}
            >
              <div className={`w-4 h-4 rounded-full mr-3 ${
                preferences.dateRange === 'last30' ? 'bg-white' : 'border-2 border-gray-500'
              }`}></div>
              Last 30 days
            </button>

            <button
              onClick={() => handleDateRangeChange('custom')}
              className={`w-full flex items-center p-3 rounded-lg transition-colors ${
                preferences.dateRange === 'custom' 
                  ? 'bg-[#7C3AED] text-white' 
                  : 'bg-transparent text-gray-300 hover:bg-gray-700/50'
              }`}
            >
              <div className={`w-4 h-4 rounded-full mr-3 ${
                preferences.dateRange === 'custom' ? 'bg-white' : 'border-2 border-gray-500'
              }`}></div>
              Custom range
            </button>
          </div>

          {preferences.dateRange === 'custom' && (
            <div className="mt-4 grid grid-cols-2 gap-3">
              <div>
                <label className="block text-sm text-gray-400 mb-2">From</label>
                <input
                  type="date"
                  value={preferences.customStartDate}
                  onChange={(e) => handleCustomDateChange('customStartDate', e.target.value)}
                  className="w-full bg-[#2A3441] border border-[#3A4553] rounded-lg px-3 py-2 text-white placeholder-gray-500"
                  placeholder="mm/dd/yyyy"
                />
              </div>
              <div>
                <label className="block text-sm text-gray-400 mb-2">To</label>
                <input
                  type="date"
                  value={preferences.customEndDate}
                  onChange={(e) => handleCustomDateChange('customEndDate', e.target.value)}
                  className="w-full bg-[#2A3441] border border-[#3A4553] rounded-lg px-3 py-2 text-white placeholder-gray-500"
                  placeholder="mm/dd/yyyy"
                />
              </div>
            </div>
          )}
        </div>

        {/* Select Data Types */}
        <div className="bg-[#1A1F2E] rounded-xl p-4 border border-[#2A3441]/30">
          <h2 className="text-white font-medium mb-4">Select Data Types</h2>

          <div className="space-y-3">
            <button
              onClick={() => handleDataTypeToggle('sleepQuality')}
              className="w-full flex items-center p-3 rounded-lg hover:bg-gray-700/50 transition-colors"
            >
              <Moon className="w-5 h-5 text-blue-400 mr-3" />
              <span className="flex-1 text-left text-white">Sleep Quality</span>
              <div className={`w-6 h-6 rounded ${
                preferences.dataTypes.sleepQuality 
                  ? 'bg-[#7C3AED] flex items-center justify-center' 
                  : 'border-2 border-gray-500'
              }`}>
                {preferences.dataTypes.sleepQuality && (
                  <svg className="w-4 h-4 text-white" fill="currentColor" viewBox="0 0 20 20">
                    <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
                  </svg>
                )}
              </div>
            </button>

            <button
              onClick={() => handleDataTypeToggle('stressLevels')}
              className="w-full flex items-center p-3 rounded-lg hover:bg-gray-700/50 transition-colors"
            >
              <Activity className="w-5 h-5 text-orange-400 mr-3" />
              <span className="flex-1 text-left text-white">Stress Levels</span>
              <div className={`w-6 h-6 rounded ${
                preferences.dataTypes.stressLevels 
                  ? 'bg-[#7C3AED] flex items-center justify-center' 
                  : 'border-2 border-gray-500'
              }`}>
                {preferences.dataTypes.stressLevels && (
                  <svg className="w-4 h-4 text-white" fill="currentColor" viewBox="0 0 20 20">
                    <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
                  </svg>
                )}
              </div>
            </button>

            <button
              onClick={() => handleDataTypeToggle('foodTriggers')}
              className="w-full flex items-center p-3 rounded-lg hover:bg-gray-700/50 transition-colors"
            >
              <Utensils className="w-5 h-5 text-green-400 mr-3" />
              <span className="flex-1 text-left text-white">Food Triggers</span>
              <div className={`w-6 h-6 rounded ${
                preferences.dataTypes.foodTriggers 
                  ? 'bg-[#7C3AED] flex items-center justify-center' 
                  : 'border-2 border-gray-500'
              }`}>
                {preferences.dataTypes.foodTriggers && (
                  <svg className="w-4 h-4 text-white" fill="currentColor" viewBox="0 0 20 20">
                    <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
                  </svg>
                )}
              </div>
            </button>

            <button
              onClick={() => handleDataTypeToggle('hrv')}
              className="w-full flex items-center p-3 rounded-lg hover:bg-gray-700/50 transition-colors"
            >
              <Heart className="w-5 h-5 text-red-400 mr-3" />
              <span className="flex-1 text-left text-white">HRV (Heart Rate Variability)</span>
              <div className={`w-6 h-6 rounded ${
                preferences.dataTypes.hrv 
                  ? 'bg-[#7C3AED] flex items-center justify-center' 
                  : 'border-2 border-gray-500'
              }`}>
                {preferences.dataTypes.hrv && (
                  <svg className="w-4 h-4 text-white" fill="currentColor" viewBox="0 0 20 20">
                    <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
                  </svg>
                )}
              </div>
            </button>
          </div>
        </div>

        {/* Export Format */}
        <div className="bg-[#1A1F2E] rounded-xl p-4 border border-[#2A3441]/30">
          <h2 className="text-white font-medium mb-4">Export Format</h2>

          <div className="space-y-3">
            <button
              onClick={() => handleFormatChange('csv')}
              className={`w-full flex items-center p-3 rounded-lg transition-colors ${
                preferences.format === 'csv' 
                  ? 'bg-[#7C3AED] text-white' 
                  : 'bg-transparent text-gray-300 hover:bg-gray-700/50'
              }`}
            >
              <div className={`w-4 h-4 rounded-full mr-3 ${
                preferences.format === 'csv' ? 'bg-white' : 'border-2 border-gray-500'
              }`}></div>
              <File className="w-5 h-5 mr-3" />
              <div className="flex-1 text-left">
                <div className="text-white font-medium">CSV File</div>
                <div className="text-gray-400 text-sm">Spreadsheet compatible format</div>
              </div>
            </button>

            <button
              onClick={() => handleFormatChange('pdf')}
              className={`w-full flex items-center p-3 rounded-lg transition-colors ${
                preferences.format === 'pdf' 
                  ? 'bg-[#7C3AED] text-white' 
                  : 'bg-transparent text-gray-300 hover:bg-gray-700/50'
              }`}
            >
              <div className={`w-4 h-4 rounded-full mr-3 ${
                preferences.format === 'pdf' ? 'bg-white' : 'border-2 border-gray-500'
              }`}></div>
              <FileText className="w-5 h-5 mr-3" />
              <div className="flex-1 text-left">
                <div className="text-white font-medium">PDF Report</div>
                <div className="text-gray-400 text-sm">Formatted document with charts</div>
              </div>
            </button>
          </div>
        </div>

        {/* Export Summary */}
        <div className="bg-[#1A1F2E] rounded-xl p-4 border border-[#2A3441]/30">
          <h2 className="text-white font-medium mb-4">Export Summary</h2>

          <div className="space-y-3">
            <div className="flex justify-between">
              <span className="text-gray-400">Date Range:</span>
              <span className="text-white">{getDateRangeText()}</span>
            </div>
            <div className="flex justify-between">
              <span className="text-gray-400">Data Types:</span>
              <span className="text-white">{getSelectedDataTypesCount()} selected</span>
            </div>
            <div className="flex justify-between">
              <span className="text-gray-400">Format:</span>
              <span className="text-white">{preferences.format.toUpperCase()}</span>
            </div>
            <div className="flex justify-between">
              <span className="text-gray-400">Estimated Size:</span>
              <span className="text-white">{getEstimatedSize()}</span>
            </div>
          </div>
        </div>

        {/* Export Button */}
        <div className="pb-8">
          <button
            onClick={handleExport}
            disabled={exportMutation.isPending || getSelectedDataTypesCount() === 0}
            className="w-full bg-[#7C3AED] hover:bg-[#6D28D9] disabled:bg-gray-600 disabled:cursor-not-allowed text-white py-4 px-6 rounded-xl font-medium flex items-center justify-center transition-colors"
          >
            {exportMutation.isPending ? (
              <>
                <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-3"></div>
                Exporting...
              </>
            ) : (
              <>
                <Download className="w-5 h-5 mr-3" />
                Export Health Data
              </>
            )}
          </button>
        </div>
      </div>
    </div>
  );
}

client/src/components/FoodHistoryScreen..content:
import React, { useState } from 'react';
import { ArrowLeft, Calendar, Filter, ChevronDown, Utensils, Clock } from 'lucide-react';
import { useLocation } from 'wouter';
import { useQuery } from '@tanstack/react-query';

interface Food {
  id: number;
  name: string;
  brand?: string;
  category: string;
  servingSize: number;
  servingUnit: string;
  caloriesPerServing: number;
  proteinPerServing: number;
  carbsPerServing: number;
  fatPerServing: number;
}

interface FoodLog {
  id: number;
  userId: number;
  foodId: number;
  quantity: number;
  unit: string;
  mealType: string;
  notes?: string;
  loggedAt: string;
  createdAt: string;
  food: Food;
}

export default function FoodHistoryScreen() {
  const [location, navigate] = useLocation();
  const [selectedFilter, setSelectedFilter] = useState('all');
  const [selectedDate, setSelectedDate] = useState('');

  // Get current user ID from localStorage token
  const getCurrentUserId = () => {
    const token = localStorage.getItem("token");
    if (token) {
      try {
        const payload = JSON.parse(atob(token.split('.')[1]));
        return payload.userId;
      } catch (error) {
        console.error("Error parsing token:", error);
        return null;
      }
    }
    return null;
  };

  const userId = getCurrentUserId();

  // Fetch user food logs
  const { data: foodLogs = [], isLoading } = useQuery({
    queryKey: ['user', 'food-logs', userId],
    queryFn: async () => {
      if (!userId) return [];
      const response = await fetch(`/api/user/food-log/recent/${userId}?limit=50`);
      if (!response.ok) throw new Error('Failed to fetch food logs');
      return response.json();
    },
    enabled: !!userId,
  });

  // Filter food logs based on selected criteria
  const filteredLogs = foodLogs.filter((log: FoodLog) => {
    if (selectedFilter !== 'all' && log.mealType !== selectedFilter) return false;
    if (selectedDate && !log.loggedAt.startsWith(selectedDate)) return false;
    return true;
  });

  // Group logs by date
  const groupedLogs: Record<string, FoodLog[]> = filteredLogs.reduce((groups: Record<string, FoodLog[]>, log: FoodLog) => {
    const date = new Date(log.loggedAt).toISOString().split('T')[0];
    if (!groups[date]) groups[date] = [];
    groups[date].push(log);
    return groups;
  }, {});

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    const today = new Date();
    const yesterday = new Date(today);
    yesterday.setDate(yesterday.getDate() - 1);

    if (date.toDateString() === today.toDateString()) return 'Today';
    if (date.toDateString() === yesterday.toDateString()) return 'Yesterday';
    return date.toLocaleDateString('en-US', { 
      weekday: 'long', 
      month: 'short', 
      day: 'numeric' 
    });
  };

  const formatTime = (dateString: string) => {
    return new Date(dateString).toLocaleTimeString('en-US', {
      hour: 'numeric',
      minute: '2-digit',
      hour12: true
    });
  };

  const getMealTypeEmoji = (mealType: string) => {
    switch (mealType.toLowerCase()) {
      case 'breakfast': return 'üåÖ';
      case 'lunch': return '‚òÄÔ∏è';
      case 'dinner': return 'üåô';
      case 'snack': return 'üç™';
      default: return 'üçΩÔ∏è';
    }
  };

  const getFoodEmoji = (foodName: string) => {
    const name = foodName.toLowerCase();
    if (name.includes('chicken') || name.includes('salad')) return 'ü•ó';
    if (name.includes('apple')) return 'üçé';
    if (name.includes('oatmeal')) return 'ü•£';
    if (name.includes('yogurt')) return 'ü•õ';
    if (name.includes('banana')) return 'üçå';
    if (name.includes('almond')) return 'ü•ú';
    if (name.includes('salmon') || name.includes('fish')) return 'üêü';
    if (name.includes('rice')) return 'üçö';
    return 'üçΩÔ∏è';
  };

  const calculateNutrition = (log: FoodLog) => {
    const multiplier = log.quantity;
    return {
      calories: Math.round(log.food.caloriesPerServing * multiplier),
      protein: Math.round(log.food.proteinPerServing * multiplier),
      carbs: Math.round(log.food.carbsPerServing * multiplier),
      fat: Math.round(log.food.fatPerServing * multiplier),
    };
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-[#2D3748] text-white flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-white mx-auto mb-4"></div>
          <p className="text-gray-400">Loading food history...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-[#2D3748] text-white">
      {/* Header */}
      <div className="flex items-center justify-between p-4 pt-16">
        <button
          onClick={() => navigate('/dashboard')}
          className="text-white"
        >
          <ArrowLeft className="w-6 h-6" />
        </button>
        <h1 className="text-lg font-medium text-white">Food History</h1>
        <div className="w-6"></div>
      </div>

      {/* Filters */}
      <div className="px-4 mb-6">
        <div className="flex space-x-3 mb-4">
          {/* Meal Type Filter */}
          <div className="relative flex-1">
            <select
              value={selectedFilter}
              onChange={(e) => setSelectedFilter(e.target.value)}
              className="w-full bg-[#4A5568] text-white rounded-xl px-4 py-3 pr-10 appearance-none outline-none"
            >
              <option value="all">All Meals</option>
              <option value="breakfast">Breakfast</option>
              <option value="lunch">Lunch</option>
              <option value="dinner">Dinner</option>
              <option value="snack">Snacks</option>
            </select>
            <ChevronDown className="absolute right-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400 pointer-events-none" />
          </div>

          {/* Date Filter */}
          <div className="relative flex-1">
            <input
              type="date"
              value={selectedDate}
              onChange={(e) => setSelectedDate(e.target.value)}
              className="w-full bg-[#4A5568] text-white rounded-xl px-4 py-3 outline-none"
            />
          </div>
        </div>

        {/* Summary Stats */}
        {filteredLogs.length > 0 && (
          <div className="bg-[#4A5568] rounded-xl p-4 mb-4">
            <div className="flex items-center space-x-2 mb-3">
              <Utensils className="w-5 h-5 text-[#7C3AED]" />
              <span className="text-white font-medium">Today's Summary</span>
            </div>
            <div className="grid grid-cols-4 gap-4">
              <div className="text-center">
                <div className="text-white font-semibold">
                  {filteredLogs.reduce((total: number, log: FoodLog) => total + calculateNutrition(log).calories, 0)}
                </div>
                <div className="text-gray-400 text-xs">Calories</div>
              </div>
              <div className="text-center">
                <div className="text-white font-semibold">
                  {filteredLogs.reduce((total: number, log: FoodLog) => total + calculateNutrition(log).protein, 0)}g
                </div>
                <div className="text-gray-400 text-xs">Protein</div>
              </div>
              <div className="text-center">
                <div className="text-white font-semibold">
                  {filteredLogs.reduce((total: number, log: FoodLog) => total + calculateNutrition(log).carbs, 0)}g
                </div>
                <div className="text-gray-400 text-xs">Carbs</div>
              </div>
              <div className="text-center">
                <div className="text-white font-semibold">
                  {filteredLogs.reduce((total: number, log: FoodLog) => total + calculateNutrition(log).fat, 0)}g
                </div>
                <div className="text-gray-400 text-xs">Fat</div>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Food Logs List */}
      <div className="px-4 pb-32">
        {Object.keys(groupedLogs).length === 0 ? (
          <div className="text-center py-16">
            <div className="w-16 h-16 bg-[#4A5568] rounded-full flex items-center justify-center mx-auto mb-4">
              <Utensils className="w-8 h-8 text-gray-400" />
            </div>
            <h3 className="text-white font-medium mb-2">No Food Logs Yet</h3>
            <p className="text-gray-400 mb-6">Start tracking your meals to see your food history here.</p>
            <button
              onClick={() => navigate('/add-food')}
              className="bg-[#7C3AED] text-white px-6 py-3 rounded-xl font-medium hover:bg-[#6D28D9] transition-colors"
            >
              Add Food
            </button>
          </div>
        ) : (
          Object.entries(groupedLogs)
            .sort(([a], [b]) => new Date(b).getTime() - new Date(a).getTime())
            .map(([date, logs]) => (
              <div key={date} className="mb-6">
                {/* Date Header */}
                <div className="flex items-center space-x-2 mb-3">
                  <Calendar className="w-4 h-4 text-[#7C3AED]" />
                  <h2 className="text-white font-medium">{formatDate(date)}</h2>
                  <div className="text-gray-400 text-sm">
                    {(logs as FoodLog[]).length} {(logs as FoodLog[]).length === 1 ? 'entry' : 'entries'}
                  </div>
                </div>

                {/* Food Entries */}
                <div className="space-y-3">
                  {(logs as FoodLog[]).map((log: FoodLog) => {
                    const nutrition = calculateNutrition(log);
                    return (
                      <div key={log.id} className="bg-[#4A5568] rounded-xl p-4">
                        <div className="flex items-start justify-between mb-3">
                          <div className="flex items-center space-x-3 flex-1">
                            <div className="w-10 h-10 bg-[#7C3AED] rounded-full flex items-center justify-center">
                              <span className="text-lg">{getFoodEmoji(log.food.name)}</span>
                            </div>
                            <div className="flex-1">
                              <div className="text-white font-medium">{log.food.name}</div>
                              <div className="text-gray-400 text-sm">
                                {log.quantity} {log.unit} ‚Ä¢ {nutrition.calories} calories
                              </div>
                              {log.food.brand && (
                                <div className="text-gray-500 text-xs">{log.food.brand}</div>
                              )}
                            </div>
                          </div>
                          <div className="text-right">
                            <div className="flex items-center space-x-1 text-gray-400 text-sm mb-1">
                              {getMealTypeEmoji(log.mealType)}
                              <span className="capitalize">{log.mealType}</span>
                            </div>
                            <div className="flex items-center space-x-1 text-gray-500 text-xs">
                              <Clock className="w-3 h-3" />
                              <span>{formatTime(log.loggedAt)}</span>
                            </div>
                          </div>
                        </div>

                        {/* Nutrition Details */}
                        <div className="grid grid-cols-4 gap-4 pt-3 border-t border-[#2D3748]">
                          <div className="text-center">
                            <div className="text-white font-semibold text-sm">{nutrition.calories}</div>
                            <div className="text-gray-400 text-xs">Cal</div>
                          </div>
                          <div className="text-center">
                            <div className="text-white font-semibold text-sm">{nutrition.protein}g</div>
                            <div className="text-gray-400 text-xs">Protein</div>
                          </div>
                          <div className="text-center">
                            <div className="text-white font-semibold text-sm">{nutrition.carbs}g</div>
                            <div className="text-gray-400 text-xs">Carbs</div>
                          </div>
                          <div className="text-center">
                            <div className="text-white font-semibold text-sm">{nutrition.fat}g</div>
                            <div className="text-gray-400 text-xs">Fat</div>
                          </div>
                        </div>

                        {/* Notes */}
                        {log.notes && (
                          <div className="mt-3 pt-3 border-t border-[#2D3748]">
                            <p className="text-gray-300 text-sm italic">"{log.notes}"</p>
                          </div>
                        )}
                      </div>
                    );
                  })}
                </div>
              </div>
            ))
        )}
      </div>

      {/* Floating Add Button */}
      <div className="fixed bottom-6 right-6">
        <button
          onClick={() => navigate('/add-food')}
          className="w-14 h-14 bg-[#7C3AED] rounded-full flex items-center justify-center shadow-lg hover:bg-[#6D28D9] transition-colors"
        >
          <Utensils className="w-6 h-6 text-white" />
        </button>
      </div>
    </div>
  );
}

client/src/components/ForgotPasswordScre.content:
import { useState } from "react";
import { useLocation } from "wouter";
import { ArrowLeft } from "lucide-react";
import { useMutation } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";

export default function ForgotPasswordScreen() {
  const [location, navigate] = useLocation();
  const { toast } = useToast();
  const [email, setEmail] = useState("");
  const [isSubmitted, setIsSubmitted] = useState(false);

  const forgotPasswordMutation = useMutation({
    mutationFn: async (email: string) => {
      const response = await fetch("/api/auth/forgot-password", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email })
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "Failed to send reset email");
      }

      return response.json();
    },
    onSuccess: () => {
      setIsSubmitted(true);
      toast({
        title: "Reset email sent",
        description: "Check your email for password reset instructions"
      });
    },
    onError: (error: any) => {
      toast({
        title: "Reset failed",
        description: error.message || "Failed to send reset email",
        variant: "destructive"
      });
    }
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!email) {
      toast({
        title: "Email required",
        description: "Please enter your email address",
        variant: "destructive"
      });
      return;
    }
    forgotPasswordMutation.mutate(email);
  };

  const handleBack = (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();
    console.log("Back button clicked - navigating to login");
    navigate("/login");
  };

  return (
    <div className="min-h-screen bg-black flex flex-col relative overflow-hidden">
      {/* Status Bar */}


      {/* Header with Back Button */}
      <div className="flex items-center justify-between px-6 pt-16 pb-8 relative">
        <button
          type="button"
          onClick={handleBack}
          className="flex items-center justify-center w-12 h-12 text-white hover:bg-gray-800 rounded-full transition-colors z-30 cursor-pointer"
          style={{ touchAction: 'manipulation' }}
        >
          <ArrowLeft className="w-6 h-6" />
        </button>
        <h1 className="absolute left-1/2 transform -translate-x-1/2 text-white text-xl font-semibold pointer-events-none">
          Forgot Password
        </h1>
        <div className="w-12 h-12"></div> {/* Spacer for centering */}
      </div>

      {/* Main Content */}
      <div className="flex-1 px-6 pb-8">
        {!isSubmitted ? (
          <form onSubmit={handleSubmit} className="space-y-6">
            <div className="text-center mb-8">
              <p className="text-gray-400 text-sm leading-relaxed">
                Enter your email address and we'll send you instructions to reset your password.
              </p>
            </div>

            {/* Email Input */}
            <div>
              <label className="block text-white text-sm font-medium mb-2">
                Email Address
              </label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Enter your email"
                className="w-full bg-gray-800 text-white placeholder-gray-400 border border-gray-700 rounded-xl px-4 py-4 focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-transparent"
                required
              />
            </div>

            {/* Submit Button */}
            <button
              type="submit"
              disabled={forgotPasswordMutation.isPending}
              className="w-full bg-purple-500 hover:bg-purple-600 disabled:bg-purple-300 text-white font-semibold py-4 px-6 rounded-2xl transition-colors"
            >
              {forgotPasswordMutation.isPending ? "Sending..." : "Send Reset Instructions"}
            </button>
          </form>
        ) : (
          <div className="text-center space-y-6">
            <div className="w-16 h-16 bg-green-500 rounded-full flex items-center justify-center mx-auto mb-6">
              <svg className="w-8 h-8 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
              </svg>
            </div>

            <h2 className="text-white text-xl font-semibold">Check Your Email</h2>

            <p className="text-gray-400 text-sm leading-relaxed px-4">
              If an account with that email exists, we've sent password reset instructions to{" "}
              <span className="text-white">{email}</span>.
            </p>

            <p className="text-gray-400 text-xs">
              Didn't receive the email? Check your spam folder or try again.
            </p>

            <button
              onClick={() => {
                setIsSubmitted(false);
                setEmail("");
              }}
              className="w-full bg-gray-800 hover:bg-gray-700 text-white font-medium py-3 rounded-xl transition-colors"
            >
              Try Different Email
            </button>
          </div>
        )}

        {/* Back to Login Link */}
        <div className="text-center mt-8">
          <p className="text-gray-400 text-sm">
            Remember your password?{" "}
            <button
              onClick={() => navigate("/login")}
              className="text-purple-400 hover:text-purple-300 font-medium"
            >
              Back to Login
            </button>
          </p>
        </div>
      </div>

      {/* Home Indicator */}
      <div className="absolute bottom-2 left-1/2 transform -translate-x-1/2 w-32 h-1 bg-white rounded-full opacity-60"></div>
    </div>
  );
}

client/src/components/HelpSupportScreen..content:
import { useState } from "react";
import { ChevronLeft, ChevronDown, ChevronRight, Phone, MessageCircle, Book, Users, PlayCircle, Send } from "lucide-react";
import { useLocation } from "wouter";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import ComingSoonPopup from "./ComingSoonPopup";

interface FAQ {
  id: number;
  question: string;
  answer: string;
  category: string;
}

interface HelpResource {
  id: number;
  title: string;
  description: string;
  type: string;
  url?: string;
  iconName: string;
}

export function HelpSupportScreen() {
  const [location, navigate] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [expandedFaq, setExpandedFaq] = useState<number | null>(null);
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const [showComingSoon, setShowComingSoon] = useState(false);
  const [comingSoonFeature, setComingSoonFeature] = useState("");

  // Fetch FAQs
  const { data: faqs = [], isLoading: faqsLoading } = useQuery<FAQ[]>({
    queryKey: ["/api/support/faqs"],
  });

  // Fetch help resources
  const { data: helpResources = [], isLoading: resourcesLoading } = useQuery<HelpResource[]>({
    queryKey: ["/api/support/help-resources"],
  });

  // Send message mutation
  const sendMessageMutation = useMutation({
    mutationFn: async ({ email, message }: { email: string; message: string }) => {
      return await apiRequest("POST", "/api/support/tickets", { 
        email, 
        message, 
        subject: "Support Request from Help Screen" 
      });
    },
    onSuccess: () => {
      toast({
        title: "Message sent successfully",
        description: "We'll get back to you within 24 hours",
      });
      setEmail("");
      setMessage("");
      queryClient.invalidateQueries({ queryKey: ["/api/support/tickets"] });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to send message. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleFaqToggle = (faqId: number) => {
    setExpandedFaq(expandedFaq === faqId ? null : faqId);
  };

  const handleResourceClick = (resourceTitle: string) => {
    setComingSoonFeature(resourceTitle);
    setShowComingSoon(true);
  };

  const handleSendMessage = () => {
    if (!email.trim() || !message.trim()) {
      toast({
        title: "Required fields",
        description: "Please fill in both email and message fields",
        variant: "destructive",
      });
      return;
    }
    sendMessageMutation.mutate({ email: email.trim(), message: message.trim() });
  };

  const handleContactSupport = () => {
    // Navigate to contact form or open email client
    window.location.href = "mailto:support@zivora.com?subject=Support Request";
  };

  const handleCallUs = () => {
    // Open phone dialer
    window.location.href = "tel:+1-800-ZIVORA";
  };

  const getResourceIcon = (iconName: string) => {
    switch (iconName) {
      case "book-open":
        return <Book className="w-5 h-5" />;
      case "users":
        return <Users className="w-5 h-5" />;
      case "play-circle":
        return <PlayCircle className="w-5 h-5" />;
      default:
        return <Book className="w-5 h-5" />;
    }
  };



  return (
    <div className="min-h-screen bg-[#2C3E50] text-white relative overflow-hidden">
      {/* Header */}
      <div className="flex items-center justify-between p-4 border-b border-gray-600">
        <button
          onClick={() => navigate("/settings")}
          className="p-2 hover:bg-gray-700 rounded-lg transition-colors"
        >
          <ChevronLeft className="w-6 h-6 text-white" />
        </button>
        <h1 className="text-lg font-medium text-white">Help & Support</h1>
        <div className="w-10"></div>
      </div>

      {/* Content */}
      <div className="p-4 space-y-6">
        {/* Contact Support Button */}
        <button
          onClick={handleContactSupport}
          className="w-full bg-gradient-to-r from-[#8B5CF6] to-[#6366F1] text-white py-4 px-6 rounded-xl font-medium text-center flex items-center justify-center space-x-2 hover:opacity-90 transition-opacity"
        >
          <MessageCircle className="w-5 h-5" />
          <span>Contact Support</span>
        </button>

        {/* Call Us Button */}
        <button
          onClick={handleCallUs}
          className="w-full bg-[#1E293B] text-white py-4 px-6 rounded-xl font-medium text-center flex items-center justify-center space-x-2 hover:bg-[#334155] transition-colors"
        >
          <Phone className="w-5 h-5" />
          <span>Call Us</span>
        </button>

        {/* Frequently Asked Questions */}
        <div className="space-y-4">
          <h2 className="text-white font-medium text-lg">Frequently Asked Questions</h2>

          <div className="space-y-3">
            {faqsLoading ? (
              <div className="text-gray-400">Loading FAQs...</div>
            ) : (
              faqs.map((faq) => (
                <div key={faq.id} className="bg-[#1A1D29] rounded-xl overflow-hidden">
                  <button
                    onClick={() => handleFaqToggle(faq.id)}
                    className="w-full p-4 text-left flex items-center justify-between hover:bg-[#1F2332] transition-colors"
                  >
                    <span className="text-white font-medium">{faq.question}</span>
                    <ChevronDown 
                      className={`w-5 h-5 text-gray-400 transition-transform ${
                        expandedFaq === faq.id ? 'rotate-180' : ''
                      }`} 
                    />
                  </button>
                  {expandedFaq === faq.id && (
                    <div className="px-4 pb-4">
                      <p className="text-gray-300 text-sm leading-relaxed">
                        {faq.answer}
                      </p>
                    </div>
                  )}
                </div>
              ))
            )}
          </div>
        </div>

        {/* Send us a message */}
        <div className="space-y-4">
          <h2 className="text-white font-medium text-lg">Send us a message</h2>

          <div className="space-y-4">
            <div>
              <Input
                type="email"
                placeholder="your-email@example.com"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="bg-[#4A5568] border-[#4A5568] text-white placeholder-gray-400 focus:ring-[#8B5CF6] focus:border-[#8B5CF6]"
              />
            </div>

            <div>
              <Textarea
                placeholder="Describe your issue or question..."
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                rows={4}
                className="bg-[#4A5568] border-[#4A5568] text-white placeholder-gray-400 focus:ring-[#8B5CF6] focus:border-[#8B5CF6] resize-none"
              />
            </div>

            <Button
              onClick={handleSendMessage}
              disabled={sendMessageMutation.isPending}
              className="w-full bg-[#6366F1] hover:bg-[#5855EB] text-white py-3 rounded-xl font-medium flex items-center justify-center space-x-2"
            >
              <Send className="w-4 h-4" />
              <span>Send Message</span>
            </Button>
          </div>
        </div>

        {/* More Resources */}
        <div className="space-y-4">
          <h2 className="text-white font-medium text-lg">More Resources</h2>

          <div className="space-y-3">
            {resourcesLoading ? (
              <div className="text-gray-400">Loading resources...</div>
            ) : (
              helpResources.map((resource) => (
                <button
                  key={resource.id}
                  onClick={() => handleResourceClick(resource.title)}
                  className="w-full bg-[#1A1D29] rounded-xl p-4 text-left flex items-center justify-between hover:bg-[#1F2332] transition-colors"
                >
                  <div className="flex items-center space-x-3">
                    <div className="text-gray-400">
                      {getResourceIcon(resource.iconName)}
                    </div>
                    <span className="text-white font-medium">{resource.title}</span>
                  </div>
                  <ChevronRight className="w-5 h-5 text-gray-400" />
                </button>
              ))
            )}
          </div>
        </div>
      </div>

      {/* Coming Soon Popup */}
      <ComingSoonPopup
        isOpen={showComingSoon}
        onClose={() => setShowComingSoon(false)}
        featureName={comingSoonFeature}
      />
    </div>
  );
}

client/src/components/HistoryScreen.tsx.content:
import React, { useState, useEffect } from 'react';
import { useLocation } from 'wouter';
import { ArrowLeft, Filter, X, Clock, ChevronRight, MoreHorizontal } from 'lucide-react';
import { useQuery } from '@tanstack/react-query';
import { LineChart, Line, XAxis, YAxis, ResponsiveContainer, AreaChart, Area } from 'recharts';

interface TrendsSummary {
  totalEpisodes: number;
  avgSeverity: number;
  avgDuration: number;
}

export default function HistoryScreen() {
  const [location, navigate] = useLocation();
  const [timePeriod, setTimePeriod] = useState('30d');
  const [showFilter, setShowFilter] = useState(false);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [selectedFilters, setSelectedFilters] = useState({
    severity: 'all',
    triggers: 'all',
    duration: 'all'
  });
  const [selectedEpisode, setSelectedEpisode] = useState<any>(null);
  const [showEpisodeDetails, setShowEpisodeDetails] = useState(false);

  // Update time every minute
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 60000); // Update every minute

    return () => clearInterval(timer);
  }, []);

  // Get time greeting based on current time
  const getTimeGreeting = () => {
    const hour = currentTime.getHours();
    if (hour < 12) return 'Good Morning';
    if (hour < 17) return 'Good Afternoon';
    return 'Good Evening';
  };

  // Format current time
  const formatTime = () => {
    return currentTime.toLocaleTimeString([], { 
      hour: '2-digit', 
      minute: '2-digit',
      hour12: true 
    });
  };

  // Get current user ID from localStorage token
  const getCurrentUserId = () => {
    const token = localStorage.getItem("token");
    if (token) {
      try {
        const payload = JSON.parse(atob(token.split('.')[1]));
        return payload.userId;
      } catch (error) {
        console.error("Error parsing token:", error);
        return null;
      }
    }
    return null;
  };

  const userId = getCurrentUserId();

  // API calls for dynamic data with time period filtering
  const { data: trendsSummary, isLoading: summaryLoading } = useQuery({
    queryKey: [`/api/user/trends/summary/${userId}`, timePeriod],
    queryFn: () => fetch(`/api/user/trends/summary/${userId}?period=${timePeriod}`)
      .then(res => res.json()),
    enabled: !!userId
  });

  const { data: recentEpisodes = [], isLoading: episodesLoading } = useQuery({
    queryKey: [`/api/user/trends/recent/${userId}`, timePeriod],
    queryFn: async () => {
      const response = await fetch(`/api/user/trends/recent/${userId}?limit=5&period=${timePeriod}`);
      const data = await response.json();
      return Array.isArray(data) ? data : [];
    },
    enabled: !!userId
  });

  const { data: frequencyTrendData = [], isLoading: frequencyLoading } = useQuery({
    queryKey: [`/api/user/trends/frequency/${userId}`, timePeriod],
    queryFn: () => fetch(`/api/user/trends/frequency/${userId}?period=${timePeriod}`)
      .then(res => res.json()),
    enabled: !!userId
  });

  const { data: correlationData, isLoading: correlationLoading } = useQuery({
    queryKey: [`/api/user/trends/correlations/${userId}`, timePeriod],
    queryFn: () => fetch(`/api/user/trends/correlations/${userId}?period=${timePeriod}`)
      .then(res => res.json()),
    enabled: !!userId
  });

  const { data: triggerPatterns = [], isLoading: triggersLoading } = useQuery({
    queryKey: [`/api/user/trends/triggers/${userId}`, timePeriod],
    queryFn: async () => {
      const response = await fetch(`/api/user/trends/triggers/${userId}?period=${timePeriod}`);
      const data = await response.json();
      return Array.isArray(data) ? data : [];
    },
    enabled: !!userId
  });

  // Fetch recent symptom logs
  const { data: recentSymptomLogs = [], isLoading: symptomLogsLoading } = useQuery({
    queryKey: [`/api/user/symptoms/${userId}`],
