import React, { useState, useEffect } from 'react';
import { ArrowLeft, Edit, Save, X, User, Mail, Phone, Calendar, MapPin, Camera } from 'lucide-react';
import { useLocation } from 'wouter';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { useToast } from '@/hooks/use-toast';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { apiRequest } from '@/lib/queryClient';

interface PersonalInfo {
  id: number;
  name: string;
  email: string;
  phone?: string;
  dateOfBirth?: string;
  address?: string;
  profileImage?: string;
  gender?: string;
  emergencyContact?: string;
  emergencyPhone?: string;
}

export default function PersonalInfoScreen() {
  const [location, navigate] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState<PersonalInfo>({
    id: 0,
    name: '',
    email: '',
    phone: '',
    dateOfBirth: '',
    address: '',
    profileImage: '',
    gender: '',
    emergencyContact: '',
    emergencyPhone: ''
  });

  // Get current user ID from localStorage token
  const getCurrentUserId = () => {
    const token = localStorage.getItem("token");
    if (token) {
      try {
        const payload = JSON.parse(atob(token.split('.')[1]));
        return payload.userId;
      } catch (error) {
        console.error("Error parsing token:", error);
        return null;
      }
    }
    return null;
  };

  const userId = getCurrentUserId();

  // Fetch personal info
  const { data: personalInfo, isLoading } = useQuery<PersonalInfo>({
    queryKey: ['personal-info', userId],
    queryFn: async () => {
      const token = localStorage.getItem("token");
      const response = await fetch(`/api/user/personal-info/${userId}`, {
        headers: { 
          "Authorization": `Bearer ${token}`,
          "Content-Type": "application/json"
        }
      });
      if (!response.ok) {
        throw new Error("Failed to fetch personal info");
      }
      return response.json();
    },
    enabled: !!userId,
  });

  // Set form data when personal info is loaded
  useEffect(() => {
    if (personalInfo) {
      setFormData(personalInfo);
    }
  }, [personalInfo]);

  // Update personal info mutation
  const updatePersonalInfoMutation = useMutation({
    mutationFn: async (data: Partial<PersonalInfo>) => {
      return apiRequest('/api/user/personal-info', 'PUT', data);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Personal information updated successfully",
      });
      setIsEditing(false);
      queryClient.invalidateQueries({ queryKey: ['personal-info'] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update personal information",
        variant: "destructive",
      });
    }
  });

  const handleSave = () => {
    updatePersonalInfoMutation.mutate(formData);
  };

  const handleCancel = () => {
    if (personalInfo) {
      setFormData(personalInfo);
    }
    setIsEditing(false);
  };

  const handleInputChange = (field: keyof PersonalInfo, value: string) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const getInitials = (name: string) => {
    return name.split(' ').map(n => n[0]).join('').toUpperCase();
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-900 flex items-center justify-center">
        <div className="text-white">Loading...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      {/* Header */}
      <div className="flex items-center justify-between p-4 bg-gray-800 sticky top-0 z-10">
        <button
          onClick={() => navigate('/settings')}
          className="flex items-center justify-center w-10 h-10 rounded-full bg-gray-700 hover:bg-gray-600 transition-colors"
        >
          <ArrowLeft className="w-5 h-5 text-white" />
        </button>
        <h1 className="text-lg font-semibold text-white">Personal Information</h1>
        <button
          onClick={() => isEditing ? handleCancel() : setIsEditing(true)}
          className="flex items-center justify-center w-10 h-10 rounded-full bg-gray-700 hover:bg-gray-600 transition-colors"
        >
          {isEditing ? <X className="w-5 h-5 text-white" /> : <Edit className="w-5 h-5 text-white" />}
        </button>
      </div>

      <div className="p-4 space-y-6">
        {/* Profile Photo Section */}
        <Card className="bg-gray-800 border-gray-700">
          <CardContent className="pt-6">
            <div className="flex flex-col items-center space-y-4">
              <Avatar className="w-24 h-24">
                <AvatarImage src={formData.profileImage} alt="Profile" />
                <AvatarFallback className="bg-purple-500 text-white text-xl">
                  {getInitials(formData.name || 'U')}
                </AvatarFallback>
              </Avatar>
              {isEditing && (
                <Button variant="outline" size="sm" className="bg-gray-700 border-gray-600 text-white hover:bg-gray-600">
                  <Camera className="w-4 h-4 mr-2" />
                  Change Photo
                </Button>
              )}
            </div>
          </CardContent>
        </Card>

        {/* Basic Information */}
        <Card className="bg-gray-800 border-gray-700">
          <CardHeader>
            <CardTitle className="text-white flex items-center">
              <User className="w-5 h-5 mr-2" />
              Basic Information
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <Label htmlFor="name" className="text-gray-300">Full Name</Label>
              <Input
                id="name"
                value={formData.name}
                onChange={(e) => handleInputChange('name', e.target.value)}
                disabled={!isEditing}
                className="bg-gray-700 border-gray-600 text-white mt-1"
              />
            </div>

            <div>
              <Label htmlFor="email" className="text-gray-300">Email</Label>
              <Input
                id="email"
                type="email"
                value={formData.email}
                onChange={(e) => handleInputChange('email', e.target.value)}
                disabled={!isEditing}
                className="bg-gray-700 border-gray-600 text-white mt-1"
              />
            </div>

            <div>
              <Label htmlFor="phone" className="text-gray-300">Phone Number</Label>
              <Input
                id="phone"
                type="tel"
                value={formData.phone || ''}
                onChange={(e) => handleInputChange('phone', e.target.value)}
                disabled={!isEditing}
                className="bg-gray-700 border-gray-600 text-white mt-1"
                placeholder="Enter your phone number"
              />
            </div>

            <div>
              <Label htmlFor="gender" className="text-gray-300">Gender</Label>
              <select
                id="gender"
                value={formData.gender || ''}
                onChange={(e) => handleInputChange('gender', e.target.value)}
                disabled={!isEditing}
                className="w-full bg-gray-700 border-gray-600 text-white mt-1 px-3 py-2 rounded-md"
              >
                <option value="">Select Gender</option>
                <option value="male">Male</option>
                <option value="female">Female</option>
                <option value="other">Other</option>
                <option value="prefer-not-to-say">Prefer not to say</option>
              </select>
            </div>

            <div>
              <Label htmlFor="dateOfBirth" className="text-gray-300">Date of Birth</Label>
              <Input
                id="dateOfBirth"
                type="date"
                value={formData.dateOfBirth || ''}
                onChange={(e) => handleInputChange('dateOfBirth', e.target.value)}
                disabled={!isEditing}
                className="bg-gray-700 border-gray-600 text-white mt-1"
              />
            </div>
          </CardContent>
        </Card>

        {/* Contact Information */}
        <Card className="bg-gray-800 border-gray-700">
          <CardHeader>
            <CardTitle className="text-white flex items-center">
              <MapPin className="w-5 h-5 mr-2" />
              Contact Information
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <Label htmlFor="address" className="text-gray-300">Address</Label>
              <Input
                id="address"
                value={formData.address || ''}
                onChange={(e) => handleInputChange('address', e.target.value)}
                disabled={!isEditing}
                className="bg-gray-700 border-gray-600 text-white mt-1"
                placeholder="Enter your address"
              />
            </div>
          </CardContent>
        </Card>

        {/* Emergency Contact */}
        <Card className="bg-gray-800 border-gray-700">
          <CardHeader>
            <CardTitle className="text-white flex items-center">
              <Phone className="w-5 h-5 mr-2" />
              Emergency Contact
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <Label htmlFor="emergencyContact" className="text-gray-300">Emergency Contact Name</Label>
              <Input
                id="emergencyContact"
                value={formData.emergencyContact || ''}
                onChange={(e) => handleInputChange('emergencyContact', e.target.value)}
                disabled={!isEditing}
                className="bg-gray-700 border-gray-600 text-white mt-1"
                placeholder="Enter emergency contact name"
              />
            </div>

            <div>
              <Label htmlFor="emergencyPhone" className="text-gray-300">Emergency Contact Phone</Label>
              <Input
                id="emergencyPhone"
                type="tel"
                value={formData.emergencyPhone || ''}
                onChange={(e) => handleInputChange('emergencyPhone', e.target.value)}
                disabled={!isEditing}
                className="bg-gray-700 border-gray-600 text-white mt-1"
                placeholder="Enter emergency contact phone"
              />
            </div>
          </CardContent>
        </Card>

        {/* Save Button */}
        {isEditing && (
          <div className="flex space-x-3 pb-6">
            <Button
              onClick={handleSave}
              disabled={updatePersonalInfoMutation.isPending}
              className="flex-1 bg-purple-600 hover:bg-purple-700 text-white"
            >
              <Save className="w-4 h-4 mr-2" />
              {updatePersonalInfoMutation.isPending ? 'Saving...' : 'Save Changes'}
            </Button>
            <Button
              onClick={handleCancel}
              variant="outline"
              className="flex-1 bg-gray-700 border-gray-600 text-white hover:bg-gray-600"
            >
              Cancel
            </Button>
          </div>
        )}
      </div>
    </div>
  );
}

client/src/components/PrivacySecurityScr.content:
import { useState } from "react";
import { useLocation } from "wouter";
import { ChevronLeft, Eye, EyeOff, Shield, Trash2, Lock, Fingerprint, Smartphone } from "lucide-react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Switch } from "@/components/ui/switch";
import { useToast } from "@/hooks/use-toast";

interface SecuritySettings {
  biometricLogin: boolean;
  twoFactorAuth: boolean;
  userId: string;
}

export function PrivacySecurityScreen() {
  const [location, navigate] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [currentPassword, setCurrentPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [showCurrentPassword, setShowCurrentPassword] = useState(false);
  const [showNewPassword, setShowNewPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  // Fetch security settings
  const { data: securitySettings, isLoading } = useQuery<SecuritySettings>({
    queryKey: ["/api/user/security-settings"],
    queryFn: async () => {
      const response = await apiRequest("GET", "/api/user/security-settings");
      return response.json();
    },
  });

  // Change password mutation
  const changePasswordMutation = useMutation({
    mutationFn: async ({ currentPassword, newPassword }: { currentPassword: string; newPassword: string }) => {
      return await apiRequest("POST", "/api/user/change-password", { 
        currentPassword,
        newPassword 
      });
    },
    onSuccess: () => {
      toast({
        title: "Password changed successfully",
        description: "Your password has been updated",
      });
      setCurrentPassword("");
      setNewPassword("");
      setConfirmPassword("");
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to change password",
        variant: "destructive",
      });
    },
  });

  // Update security settings mutation
  const updateSecurityMutation = useMutation({
    mutationFn: async (settings: Partial<SecuritySettings>) => {
      return await apiRequest("PATCH", "/api/user/security-settings", settings);
    },
    onSuccess: () => {
      toast({
        title: "Security settings updated",
        description: "Your security preferences have been saved",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/user/security-settings"] });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update security settings",
        variant: "destructive",
      });
    },
  });

  // Delete account mutation
  const deleteAccountMutation = useMutation({
    mutationFn: async () => {
      return await apiRequest("DELETE", "/api/user/account");
    },
    onSuccess: () => {
      toast({
        title: "Account deleted",
        description: "Your account has been permanently deleted",
      });
      navigate("/login");
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete account",
        variant: "destructive",
      });
    },
  });

  const handleSaveChanges = () => {
    if (currentPassword && newPassword && confirmPassword) {
      if (newPassword !== confirmPassword) {
        toast({
          title: "Password mismatch",
          description: "New password and confirmation don't match",
          variant: "destructive",
        });
        return;
      }

      if (newPassword.length < 8) {
        toast({
          title: "Password too short",
          description: "Password must be at least 8 characters long",
          variant: "destructive",
        });
        return;
      }

      changePasswordMutation.mutate({ currentPassword, newPassword });
    } else if (!currentPassword && !newPassword && !confirmPassword) {
      // Just save other settings if no password change
      toast({
        title: "Settings saved",
        description: "Your security settings have been updated",
      });
    } else {
      toast({
        title: "Incomplete form",
        description: "Please fill in all password fields or leave them empty",
        variant: "destructive",
      });
    }
  };

  const handleBiometricToggle = (enabled: boolean) => {
    updateSecurityMutation.mutate({ biometricLogin: enabled });
  };

  const handleTwoFactorToggle = (enabled: boolean) => {
    updateSecurityMutation.mutate({ twoFactorAuth: enabled });
  };

  const handleDeleteAccount = () => {
    if (window.confirm("Are you sure you want to permanently delete your account? This action cannot be undone.")) {
      deleteAccountMutation.mutate();
    }
  };

  const isFormValid = () => {
    const hasPasswordChange = currentPassword || newPassword || confirmPassword;
    if (hasPasswordChange) {
      return currentPassword && newPassword && confirmPassword && newPassword === confirmPassword;
    }
    return true;
  };

  const validatePassword = (password: string) => {
    return {
      minLength: password.length >= 8,
      uppercase: /[A-Z]/.test(password),
      lowercase: /[a-z]/.test(password),
      number: /\d/.test(password),
      special: /[!@#$%^&*(),.?":{}|<>]/.test(password)
    };
  };

  const passwordValidation = validatePassword(newPassword);

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-900 text-white flex items-center justify-center">
        <div className="text-gray-400">Loading...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-900 text-white relative overflow-hidden">
      {/* Header */}
      <div className="bg-gray-800 px-4 py-4 flex items-center justify-between">
        <button 
          onClick={() => navigate('/settings')}
          className="p-2 hover:bg-gray-700 rounded-full transition-colors"
        >
          <ChevronLeft className="w-6 h-6 text-white" />
        </button>
        <h1 className="text-lg font-semibold">Privacy & Security</h1>
        <div className="w-10 h-10" /> {/* Spacer */}
      </div>

      {/* Content */}
      <div className="p-6 space-y-8">
        {/* Change Password Section */}
        <div className="space-y-4">
          <h2 className="text-white font-medium text-lg">Change Password</h2>

          {/* Current Password */}
          <div className="space-y-2">
            <label className="text-gray-300 text-sm">Current Password</label>
            <div className="relative">
              <Input
                type={showCurrentPassword ? "text" : "password"}
                value={currentPassword}
                onChange={(e) => setCurrentPassword(e.target.value)}
                placeholder="Enter current password"
                className="bg-gray-800 border-gray-700 text-white placeholder-gray-400 focus:ring-[#8B5CF6] focus:border-[#8B5CF6] pr-12"
              />
              <button
                type="button"
                onClick={() => setShowCurrentPassword(!showCurrentPassword)}
                className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-white transition-colors"
              >
                {showCurrentPassword ? <EyeOff className="w-5 h-5" /> : <Eye className="w-5 h-5" />}
              </button>
            </div>
          </div>

          {/* New Password */}
          <div className="space-y-2">
            <label className="text-gray-300 text-sm">New Password</label>
            <div className="relative">
              <Input
                type={showNewPassword ? "text" : "password"}
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                placeholder="Enter new password"
                className="bg-gray-800 border-gray-700 text-white placeholder-gray-400 focus:ring-[#8B5CF6] focus:border-[#8B5CF6] pr-12"
              />
              <button
                type="button"
                onClick={() => setShowNewPassword(!showNewPassword)}
                className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-white transition-colors"
              >
                {showNewPassword ? <EyeOff className="w-5 h-5" /> : <Eye className="w-5 h-5" />}
              </button>
            </div>
          </div>

          {/* Confirm New Password */}
          <div className="space-y-2">
            <label className="text-gray-300 text-sm">Confirm New Password</label>
            <div className="relative">
              <Input
                type={showConfirmPassword ? "text" : "password"}
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                placeholder="Confirm new password"
                className="bg-gray-800 border-gray-700 text-white placeholder-gray-400 focus:ring-[#8B5CF6] focus:border-[#8B5CF6] pr-12"
              />
              <button
                type="button"
                onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-white transition-colors"
              >
                {showConfirmPassword ? <EyeOff className="w-5 h-5" /> : <Eye className="w-5 h-5" />}
              </button>
            </div>
          </div>

          {/* Password Requirements */}
          <div className="space-y-2">
            <label className="text-gray-300 text-sm">Password requirements:</label>
            <div className="space-y-1 text-xs text-gray-400">
              <div className="flex items-center space-x-2">
                <div className={`w-2 h-2 rounded-full ${newPassword.length >= 8 ? 'bg-green-500' : 'bg-gray-500'}`} />
                <span>Minimum 8 characters</span>
              </div>
              <div className="flex items-center space-x-2">
                <div className={`w-2 h-2 rounded-full ${/[A-Z]/.test(newPassword) ? 'bg-green-500' : 'bg-gray-500'}`} />
                <span>At least one uppercase letter</span>
              </div>
              <div className="flex items-center space-x-2">
                <div className={`w-2 h-2 rounded-full ${/[a-z]/.test(newPassword) ? 'bg-green-500' : 'bg-gray-500'}`} />
                <span>At least one lowercase letter</span>
              </div>
              <div className="flex items-center space-x-2">
                <div className={`w-2 h-2 rounded-full ${/[0-9]/.test(newPassword) ? 'bg-green-500' : 'bg-gray-500'}`} />
                <span>At least one number</span>
              </div>
              <div className="flex items-center space-x-2">
                <div className={`w-2 h-2 rounded-full ${/[^A-Za-z0-9]/.test(newPassword) ? 'bg-green-500' : 'bg-gray-500'}`} />
                <span>At least one special character</span>
              </div>
            </div>
          </div>
        </div>

        {/* Security Settings */}
        <div className="space-y-4">
          <h2 className="text-white font-medium text-lg">Security Settings</h2>

          {/* Biometric Login */}
          <div className="bg-gray-800 rounded-xl p-4 flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <Fingerprint className="w-5 h-5 text-[#8B5CF6]" />
              <div>
                <span className="text-white font-medium block">Biometric Login</span>
                <span className="text-gray-400 text-sm">Log in using fingerprint or face ID</span>
              </div>
            </div>
            <Switch
              checked={securitySettings?.biometricLogin || false}
              onCheckedChange={handleBiometricToggle}
              className="data-[state=checked]:bg-[#8B5CF6]"
            />
          </div>

          {/* Two-Factor Authentication */}
          <div className="bg-gray-800 rounded-xl p-4 flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <Smartphone className="w-5 h-5 text-[#8B5CF6]" />
              <div>
                <span className="text-white font-medium block">Two-Factor Authentication</span>
                <span className="text-gray-400 text-sm">Extra security for your account</span>
              </div>
            </div>
            <Switch
              checked={securitySettings?.twoFactorAuth || false}
              onCheckedChange={handleTwoFactorToggle}
              className="data-[state=checked]:bg-[#8B5CF6]"
            />
          </div>
        </div>

        {/* Account Management */}
        <div className="space-y-4">
          <h2 className="text-white font-medium text-lg">Account Management</h2>

          {/* Delete Account */}
          <button
            onClick={handleDeleteAccount}
            className="w-full bg-gray-800 rounded-xl p-4 text-left flex items-center justify-between hover:bg-gray-700 transition-colors group"
          >
            <div className="flex items-center space-x-3">
              <Trash2 className="w-5 h-5 text-red-500" />
              <div>
                <span className="text-white font-medium block">Delete Account</span>
                <span className="text-gray-400 text-sm">Permanently delete your account</span>
              </div>
            </div>
          </button>
        </div>

        {/* Save Changes Button */}
        <div className="pt-6">
          <Button
            onClick={handleSaveChanges}
            disabled={!isFormValid() || changePasswordMutation.isPending}
            className="w-full bg-gradient-to-r from-[#8B5CF6] to-[#6366F1] hover:opacity-90 text-white py-4 rounded-xl font-medium text-lg transition-opacity"
          >
            {changePasswordMutation.isPending ? "Saving..." : "Save Changes"}
          </Button>
        </div>
      </div>
    </div>
  );
}

export default PrivacySecurityScreen;

client/src/components/RiskScoreScreen.ts.content:
import React from "react";
import { useLocation } from "wouter";
import { ArrowLeft, Settings, Heart, Brain, Moon, Activity, CloudRain, Droplets, BarChart3, BookOpen } from "lucide-react";
import { useQuery } from "@tanstack/react-query";
import { CircularProgressbarWithChildren, buildStyles } from "react-circular-progressbar";
import "react-circular-progressbar/dist/styles.css";
import BottomNavigation from './BottomNavigation';

export default function RiskScoreScreen() {
  const [location, navigate] = useLocation();

  // Get authenticated user ID from token
  const getUserIdFromToken = () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) return null;
      const payload = JSON.parse(atob(token.split('.')[1]));
      return payload.userId;
    } catch {
      return null;
    }
  };

  const userId = getUserIdFromToken() || 2;

  // Fetch risk score data using authenticated API
  const { data: riskData, isLoading: riskLoading } = useQuery({
    queryKey: [`/api/user/risk-score`],
    queryFn: async () => {
      const token = localStorage.getItem("token");
      const response = await fetch(`/api/user/risk-score`, {
        headers: { "Authorization": `Bearer ${token}` }
      });
      if (!response.ok) throw new Error('Failed to fetch risk score');
      return response.json();
    }
  });

  // Fetch health indicators data
  const { data: healthData } = useQuery({
    queryKey: [`/api/user/health-indicators/${userId}`],
    queryFn: async () => {
      const token = localStorage.getItem("token");
      const response = await fetch(`/api/user/health-indicators/${userId}`, {
        headers: { "Authorization": `Bearer ${token}` }
      });
      if (!response.ok) throw new Error('Failed to fetch health indicators');
      return response.json();
    }
  });

  // Fetch environmental triggers data
  const { data: triggersData } = useQuery({
    queryKey: [`/api/user/environmental-triggers/${userId}`],
    queryFn: async () => {
      const token = localStorage.getItem("token");
      const response = await fetch(`/api/user/environmental-triggers/${userId}`, {
        headers: { "Authorization": `Bearer ${token}` }
      });
      if (!response.ok) throw new Error('Failed to fetch environmental triggers');
      return response.json();
    }
  });

  if (riskLoading) {
    return (
      <div className="min-h-screen bg-[#0F172A] flex items-center justify-center">
        <div className="text-white text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-purple-500 mx-auto mb-4"></div>
          <p>Loading Risk Score...</p>
        </div>
      </div>
    );
  }

  const riskScore = riskData?.score || 32;
  const riskLevel = riskData?.level || "Low Risk";
  const riskZone = riskData?.message || "Safe Zone";

  return (
    <div className="min-h-screen bg-[#0F172A] text-white pb-20">
      {/* Header */}
      <div className="flex items-center justify-between p-4 bg-[#1E293B]">
        <button
          onClick={() => navigate('/dashboard')}
          className="flex items-center justify-center w-10 h-10 rounded-full bg-gray-700 hover:bg-gray-600 transition-colors"
        >
          <ArrowLeft className="w-5 h-5 text-white" />
        </button>
        <h1 className="text-lg font-semibold text-white">Risk Score</h1>
        <div className="flex items-center space-x-2">
          <div className="w-8 h-8 bg-purple-500 rounded-full flex items-center justify-center">
            <Activity className="w-4 h-4 text-white" />
          </div>
          <button
            onClick={() => navigate('/settings')}
            className="flex items-center justify-center w-10 h-10 rounded-full bg-gray-700 hover:bg-gray-600 transition-colors"
          >
            <Settings className="w-5 h-5 text-white" />
          </button>
        </div>
      </div>

      {/* Risk Score Card Section with Purple Gradient */}
      <div className="p-4" style={{ background: 'linear-gradient(135deg, #8B5CF6 0%, #7C3AED 50%, #6D28D9 100%)' }}>
        {/* Risk Score Card */}
        <div className="w-full max-w-sm mx-auto p-6 rounded-2xl bg-[#11101A] shadow-xl text-white">
          <h2 className="text-center text-sm font-semibold mb-4 tracking-wide">
            Today's Migraine Risk Score
          </h2>

          <div className="w-36 h-36 mx-auto mb-4">
            <CircularProgressbarWithChildren
              value={riskScore}
              strokeWidth={10}
              styles={buildStyles({
                pathColor: "url(#gradient)",
                trailColor: "#2E2E3A",
                strokeLinecap: "round",
              })}
            >
              <svg style={{ height: 0 }}>
                <defs>
                  <linearGradient id="gradient" gradientTransform="rotate(90)">
                    <stop offset="0%" stopColor="#A66CFF" />
                    <stop offset="100%" stopColor="#7F5AF0" />
                  </linearGradient>
                </defs>
              </svg>
              <div className="text-center">
                <p className="text-3xl font-bold">{riskScore}</p>
                <p className="text-sm text-gray-300">{riskLevel}</p>
              </div>
            </CircularProgressbarWithChildren>
          </div>

          <div className="flex justify-center mb-2">
            <span className="text-xs px-4 py-1 rounded-full bg-gradient-to-r from-purple-400 to-purple-600 text-white shadow-md">
              {riskZone}
            </span>
          </div>

          <p className="text-center text-xs text-gray-400">
            Your brain is in a stable state today. Light triggers are manageable.
          </p>
        </div>
      </div>

      {/* Rest of content with normal dark background */}
      <div className="p-4 space-y-6">
        {/* Health Indicators */}
        <div>
          <h3 className="text-white font-semibold mb-4">Health Indicators</h3>
          <div className="grid grid-cols-3 gap-3">
            <div className="bg-[#1E293B] rounded-xl p-4 text-center">
              <div className="w-8 h-8 bg-red-500 rounded-full mx-auto mb-3 flex items-center justify-center">
                <Heart className="w-4 h-4 text-white" />
              </div>
              <p className="text-white text-lg font-semibold">{healthData?.heartRate?.value || "42ms"}</p>
              <p className="text-gray-400 text-xs">{healthData?.heartRate?.status || "Good"}</p>
            </div>
            <div className="bg-[#1E293B] rounded-xl p-4 text-center">
              <div className="w-8 h-8 bg-purple-500 rounded-full mx-auto mb-3 flex items-center justify-center">
                <Brain className="w-4 h-4 text-white" />
              </div>
              <p className="text-white text-lg font-semibold">{healthData?.stressLevel?.value || "Low"}</p>
              <p className="text-gray-400 text-xs">{healthData?.stressLevel?.status || "Stable"}</p>
            </div>
            <div className="bg-[#1E293B] rounded-xl p-4 text-center">
              <div className="w-8 h-8 bg-blue-500 rounded-full mx-auto mb-3 flex items-center justify-center">
                <Moon className="w-4 h-4 text-white" />
              </div>
              <p className="text-white text-lg font-semibold">{healthData?.sleepQuality?.value || "7.2h"}</p>
              <p className="text-gray-400 text-xs">{healthData?.sleepQuality?.status || "Quality"}</p>
            </div>
          </div>
        </div>

        {/* Environmental Triggers */}
        <div>
          <h3 className="text-white font-semibold mb-4">Environmental Triggers</h3>
          <div className="space-y-3">
            {triggersData?.map((trigger: any, index: number) => {
              const getIcon = (name: string) => {
                if (name.toLowerCase().includes('pressure')) return Activity;
                if (name.toLowerCase().includes('weather')) return CloudRain;
                if (name.toLowerCase().includes('humidity')) return Droplets;
                return Activity;
              };

              const getIconColor = (status: string) => {
                if (status.toLowerCase() === 'stable') return 'bg-blue-500';
                if (status.toLowerCase() === 'low') return 'bg-orange-500';
                if (status.toLowerCase() === 'normal') return 'bg-teal-500';
                return 'bg-gray-500';
              };

              const getStatusColor = (status: string) => {
                if (status.toLowerCase() === 'stable') return 'text-blue-400';
                if (status.toLowerCase() === 'low') return 'text-green-400';
                if (status.toLowerCase() === 'normal') return 'text-gray-400';
                return 'text-gray-400';
              };

              const Icon = getIcon(trigger.name);

              return (
                <div key={index} className="bg-[#1E293B] rounded-xl p-4 flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <div className={`w-8 h-8 ${getIconColor(trigger.status)} rounded-full flex items-center justify-center`}>
                      <Icon className="w-4 h-4 text-white" />
                    </div>
                    <div>
                      <p className="text-white font-medium">{trigger.name}</p>
                      <p className="text-gray-400 text-sm">{trigger.value}</p>
                    </div>
                  </div>
                  <span className={`text-sm font-medium ${getStatusColor(trigger.status)}`}>{trigger.status}</span>
                </div>
              );
            }) || [
              <div key="loading" className="bg-[#1E293B] rounded-xl p-4 flex items-center justify-center">
                <span className="text-gray-400 text-sm">Loading environmental data...</span>
              </div>
            ]}
          </div>
        </div>

        {/* Action Buttons */}
        <div className="space-y-3">
          <button 
            onClick={() => navigate('/insights')}
            className="w-full bg-[#1E293B] hover:bg-gradient-to-r hover:from-purple-500 hover:to-purple-600 text-white font-semibold py-4 px-6 rounded-2xl transition-all duration-200 flex items-center justify-center gap-2"
          >
            <BarChart3 className="w-5 h-5" />
            Detailed Insights
          </button>

          <button 
            onClick={() => navigate('/daily-log')}
            className="w-full bg-[#1E293B] hover:bg-[#334155] text-white font-semibold py-4 px-6 rounded-2xl transition-all duration-200 flex items-center justify-center gap-2"
          >
            üìä Log Today's Data
          </button>

          <button 
            onClick={() => navigate('/education')}
            className="w-full bg-[#1E293B] hover:bg-[#334155] text-white font-semibold py-4 px-6 rounded-2xl transition-all duration-200 flex items-center justify-center gap-2"
          >
            <BookOpen className="w-5 h-5" />
            Educational Content
          </button>
        </div>
      </div>

      <BottomNavigation />
    </div>
  );
}

